import React from "react";
import { Card } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
const axios = require("axios").default;
const deletePostOnClick = async (id) => {
  try {
    axios
      .delete("http://localhost:3002/posts/", {
        data: { id: id },
      })
      .then((response) => props.sendGetRequest());
  } catch (error) {
    console.log(error);
  }

  console.log(id);
};
const Post = (props) => {
  const { id } = useParams();
  const foundPost = props.showPost.find((post) => id === post.id);
  return (
    <Card style={{ width: "18rem" }}>
      <Card.Body>
        <Card.Title>{foundPost.title}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
        <Card.Text>{foundPost.content}</Card.Text>
        <Card.Link
          onClick={() => {
            deletePostOnClick(post.id);
          }}
        >
          delete
        </Card.Link>
        <Card.Link href="#">Edit</Card.Link>
      </Card.Body>
    </Card>
  );
};

export default Post;
