import React from "react";
import { Card } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
const axios = require("axios").default;

const Post = (props) => {
  const { id } = useParams();
  const foundPost = props.showPost.find((post) => id === post.id);
  const deletePostOnClick = async (id) => {
    try {
      axios
        .delete("http://localhost:3002/posts/", {
          data: { id: id },
        })
        .then((response) => props.sendGetRequest());
    } catch (error) {
      console.log(error);
    }

    console.log(id);
  };

  return (
    <Card style={{ width: "18rem" }}>
      <Card.Body>
        <Card.Title>{foundPost.title}</Card.Title>
        <Card.Img variant="top" src="{" />
        <Card.Text
          dangerouslySetInnerHTML={{ __html: foundPost.content }}
        ></Card.Text>
        <Card.Link
          href="/"
          onClick={() => {
            deletePostOnClick(foundPost.id);
          }}
        >
          delete
        </Card.Link>
        <Card.Link href={`/editpost/${foundPost.id}`}>Edit</Card.Link>
      </Card.Body>
    </Card>
  );
};

export default Post;
