import React, { useEffect, useRef, useState } from "react";
import { Button, Form } from "react-bootstrap";
import ReactQuill from "react-quill";
import { Link, useParams } from "react-router-dom";
const axios = require("axios").default;
const EditPost = (props) => {
  const { id } = useParams();
  const [title, setTitle] = useState();
  const [content, setContent] = useState();

  const inputTitleRef = useRef();
  const inputRef = useRef();

  useEffect(() => {
    const foundPost = props.edit.find((post) => id === post._id);
    if (foundPost && id) {
      setTitle(foundPost.Title);
      setContent(foundPost.Content);
    }
  }, []);

  const handleBody = (data) => {
    setContent(data);
  };
  const titleValue = (title) => {
    if (!title) {
      const foundPost = props.edit.find((post) => post._id === id);
      if (foundPost) {
        return foundPost.Title;
      }
    } else {
      return title;
    }
  };
  const quillValue = (quillContent) => {
    if (!quillContent) {
      const foundPost = props.edit.find((post) => post._id === id);
      if (foundPost) {
        return foundPost.content;
      }
    } else {
      return quillContent;
    }
  };
  const updateBlogs = async (id, title, content) => {
    var data = { id, title, content };
    try {
      axios.put(`http://localhost:3002/posts/${id}`, data).then((res) => {
        props.sendGetRequest();
        window.location.replace("/mysite");
      });
    } catch (error) {
      console.log(error);
    }
    console.log(data);
  };
  return (
    <div>
      <div className="background-container">
        <img src="../img/image.jpg " />
      </div>
      <Form className="m-5">
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Post title</Form.Label>
          <Form.Control
            value={titleValue(title)}
            onChange={(e) => setTitle(e.target.value)}
            ref={inputTitleRef}
            placeholder="add the title"
            className="mt-2 mb-5"
          />
        </Form.Group>
        <Form.Group>
          <div className="quill">
            <ReactQuill
              value={quillValue(content)}
              onChange={handleBody}
              ref={inputRef}
              placeholder="write new post .."
              modules={EditPost.modules}
              formats={EditPost.formats}
              onChange={handleBody}
            />
          </div>
        </Form.Group>
        <Button
          onClick={() => {
            updateBlogs(id, title, content);
          }}
        >
          Save
        </Button>
      </Form>
    </div>
  );
};
EditPost.modules = {
  toolbar: [
    [{ header: "1" }, { header: "2" }, { font: [] }],
    [{ size: [] }],
    ["bold", "italic", "underline", "strike", "blockquote"],
    [
      { list: "ordered" },
      { list: "bullet" },
      { indent: "-1" },
      { indent: "+1" },
    ],
    ["link", "image", "video"],
    ["clean"],
  ],
};

EditPost.formats = [
  "header",
  "font",
  "size",
  "bold",
  "italic",
  "underline",
  "strike",
  "blockquote",
  "list",
  "bullet",
  "indent",
  "link",
  "image",
  "video",
];
export default EditPost;
