import React from "react";
import { Button, Card } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
const axios = require("axios").default;

const Post = (props) => {
  const { id } = useParams();
  const foundPost = props.showPost.find((post) => id === post.id);
  const deletePostOnClick = async (id) => {
    try {
      axios
        .delete("http://localhost:3002/posts/", {
          data: { id: id },
        })
        .then((response) => props.sendGetRequest());
    } catch (error) {
      console.log(error);
    }

    console.log(id);
  };

  return (
    <div className="post-main-container">
      <div className="background-container">
        <img src="../img/pencils-762555_1920.jpg" />
      </div>
      <Card style={{ width: "18rem" }}>
        <Card.Body>
          <Card.Title>{foundPost.title}</Card.Title>
          <Card.Text
            dangerouslySetInnerHTML={{ __html: foundPost.content }}
          ></Card.Text>
          <Button
            href="/"
            onClick={() => {
              deletePostOnClick(foundPost.id);
            }}
          >
            delete
          </Button>
          <Button href={`/editpost/${foundPost.id}`}>Edit</Button>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Post;
