import React from "react";
import "../css/style.css";
import { Link } from "react-router-dom";
import baseUrl from "../baseurl";
const axios = require("axios").default;
const MySite = (props) => {
  const deletePostOnClick = async (id) => {
    try {
      axios
        .delete(`${baseUrl}/posts/${id}`, {
          headers: {
            auth: localStorage.getItem("token"),
          },
        })
        .then((response) => {
          props.sendGetRequest();
          response.data === "notauth"
            ? alert("Not authorize to delete others post")
            : window.location.replace("/mysite");
        });
    } catch (error) {
      console.log(error);
    }
    console.log(id);
  };

  return (
    <div className="post-collection-container">
      <div className="background-container">
        <img src="../img/pencils-762555_1920.jpg" />
      </div>
      <h2 className="posts-header">posts Collection</h2>
      {props.show.length < 1 ? (
        <p style={{ textAlign: "center", fontSize: "1.3rem" }}>
          There is no post yet
        </p>
      ) : (
        props.show.map((item, index) => (
            <>
                  <Link key={index} to={`/posts/${item._id}`} className="link">
                    <h2>{item.title}</h2>
                  </Link>

                  <p>posted by {item.userId}</p>
                </>


        ))
      )
    }
    </div>
  );
};
export default MySite;
