import React, { useState } from "react";
import axios from "axios";
import { Button, Modal } from "react-bootstrap";
import { Link } from "react-router-dom";

const Header = () => {
  const [signin, setSignin] = useState(false);
  const [signup, setSignup] = useState(false);
  const handleCloseSignin = () => setSignin(false);
  const handleShowSignin = () => setSignin(true);
  const handleCloseSignup = () => setSignup(false);
  const handleShowSignup = () => setSignup(true);
  const [userSignup, setUserSignup] = useState({});
  const [userSignin, setUserSignin] = useState({});
  const [passwordShown, setPasswordShown] = useState(false);

  const myStorage = window.localStorage;

  console.log(userSignup);

  const [signUp, setsignUp] = useState(false);

  const submitSignup = async () => {

    try {

      await axios
        .post("https://kinjals-blog.herokuapp.com/users", userSignup)
        .then((response) =>
          myStorage.setItem("token", response.headers.auth)
        );
      window.location.replace("/");


    } catch (error) {
      console.log(error.response);


    }
  }

  const submitSignin = async () => {
    try {
      const response = await axios
        .post("https://localhost:3001/users/login", { "email": userSignin.email, "password": userSignin.password },)
      myStorage.setItem("token", response.headers.auth);
      console.log(response)

      window.location.replace("/");
      console.log(userSignin);
    } catch (error) {
      console.log(error.response);

    }
  };
  const signOutOnClick = () => {
    localStorage.removeItem("token")
  }

  return (
    <div className="header-main-container">
      <ul className="header-container">
        <li className="list-item">
          <Link to="/" className="header-item">
            Home /
          </Link>
        </li>
        <li className="list-item">
          <Link to="/mysite" className="header-item">
            MySite /
          </Link>
        </li>
        <li className="list-item">
          <Link to="/addpost" className="header-item">
            NewPost /
          </Link>
        </li>
      </ul>
      <div className="button-container">
        <Button className="signInBtn" onClick={handleShowSignin}>
          sign in
        </Button>
        <Button className="signInBtn" onClick={handleShowSignup}>
          sign up
        </Button>
      </div>
      <Modal
        show={signup}
        onHide={handleCloseSignup}
        backdrop="static"
        keyboard={false}
        animation={false}
      >
        <Modal.Header closeButton>
          <Modal.Title className="modalTitle">Sign up</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <label for="email" className="labelClass">
            Email:
          </label>
          <input
            className="inputClass"
            id="email"
            name="email"
            placeholder="enter your email"
          />
          <br />
          <label for="password" className="labelClass">
            Password:
          </label>
          <input
            className="inputClass"
            id="password"
            name="password"
            placeholder="enter your password"
          />
          <br />
          <label className="labelClass">Confirm Password:</label>
          <input className="inputClass" placeholder="confirm password" />
          <Button className="mt-5 modalButton">Sign up</Button>
        </Modal.Body>
        <Modal.Footer>
          <p className="modalP">Already have an account?</p>
          <Button
            className="modalButton"
            variant="primary"
            onClick={handleShowSignup}
          >
            Sign in here
          </Button>
        </Modal.Footer>
      </Modal>
      <Modal
        show={signin}
        onHide={handleCloseSignin}
        backdrop="static"
        keyboard={false}
        animation={false}
      >
        <Modal.Header closeButton>
          <Modal.Title className="modalTitle mb-4">Sign In</Modal.Title>
        </Modal.Header>
        <Modal.Body className="modalBody">
          <label for="email" className="labelClass">
            Email:
          </label>
          <input
            className="inputClass"
            id="email"
            name="email"
            placeholder="enter your email"
          />
          <br />
          <label for="password" className="labelClass">
            Password:
          </label>
          <input
            className="inputClass"
            id="password"
            name="password"
            placeholder="enter your password"
          />
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="primary"
            className="modalButtonSignIn"
            style={{ marginRight: "auto" }}
            onClick={handleShowSignin}
          >
            Sign In
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Header;
