import React, { useRef, useState } from "react";
import { Button, Form } from "react-bootstrap";
import ReactQuill from "react-quill";
import { Link, useParams } from "react-router-dom";
const axios = require("axios").default;
const EditPost = (props) => {
  const { id } = useParams();
  const foundPost = props.showPost.find((post) => id === post.id);
  const [body, setBody] = useState("");
  const handleBody = (e) => {
    inputRef.current.value = e;
  };

  const inputTitleRef = useRef();
  const inputRef = useRef();

  const Edit = async (postTitle, postContent) => {
    // TODO
    try {
      axios
        .post("http://localhost:3002/posts/", {
          title: postTitle,
          content: postContent,
        })
        .then((response) => {
          props.sendGetRequest({ body });
        });
    } catch (error) {
      console.log(error);
    }
  };

  const addPostOnClick = async () => {
    Edit(inputTitleRef.current.value, inputRef.current.value);
    setBody("");
  };

  return (
    <div>
      <Form className="m-5">
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Post title</Form.Label>
          <Form.Control
            value={foundPost.title}
            ref={inputTitleRef}
            placeholder="add the title"
            className="mt-2 mb-5"
          />
        </Form.Group>
        <Form.Group>
          <div className="quill">
            <ReactQuill
              value={foundPost.content}
              ref={inputRef}
              placeholder="write new post .."
              modules={AddPost.modules}
              formats={AddPost.formats}
              onChange={handleBody}
            />
          </div>
        </Form.Group>
        <Link to="/">
          <Button
            onClick={() => addPostOnClick()}
            variant="primary"
            type="submit"
          >
            Save
          </Button>
        </Link>
      </Form>
    </div>
  );
};
EditPost.modules = {
  toolbar: [
    [{ header: "1" }, { header: "2" }, { font: [] }],
    [{ size: [] }],
    ["bold", "italic", "underline", "strike", "blockquote"],
    [
      { list: "ordered" },
      { list: "bullet" },
      { indent: "-1" },
      { indent: "+1" },
    ],
    ["link", "image", "video"],
    ["clean"],
  ],
};
/*
 * Quill editor formats
 * See https://quilljs.com/docs/formats/
 */
EditPost.formats = [
  "header",
  "font",
  "size",
  "bold",
  "italic",
  "underline",
  "strike",
  "blockquote",
  "list",
  "bullet",
  "indent",
  "link",
  "image",
  "video",
];
export default EditPost;
